import React, { useState, useEffect } from 'react';
import './home.css'; // Import your CSS file for styling
import { useNavigate, useLocation } from 'react-router-dom';
import LineGraph from './graph';
import searchIcon from '../images/search.png'; // Adjust the path to your image
import { API_ROUTES } from '../app modules/apiRoutes';
import Navbar from '../componenets/Navbar';
import settingsImg from '../images/settings.png';
import premiumIcon from '../images/premium.png';
import axios from 'axios';

const HomePage = () => {
  const [user, setUser] = useState({});
  const [registrations, setRegistrations] = useState([]);
  const [joinedEventsCount, setJoinedEventsCount] = useState(0);
  const [registrationsCount, setRegistrationsCount] = useState(0);
  const [userData, setUserData] = useState(null);
  const [advanceBookings, setAdvanceBookings] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Token not found.');
      setLoading(false);
      return;
    }

    const fetchData = async () => {
      try {
        // Fetch user details
        const userDetailsResponse = await fetchUserData(token);
        setUser(userDetailsResponse);

        // Fetch user events count
        const userEventsResponse = await fetchUserEventCount(token);
        setUserData(userEventsResponse);

        // Fetch joined events count
        const joinedEventsResponse = await fetchJoinedEventsCount(token);
        setJoinedEventsCount(joinedEventsResponse.count);

        // Fetch registrations count
        const registrationsCountResponse = await fetchRegistrationsCount(token);
        setRegistrationsCount(registrationsCountResponse.phoneNumberCount);

        // Fetch event attendees
        const registrationsResponse = await fetchEventAttendees(token);
        setRegistrations(registrationsResponse);

        // Fetch advance bookings
        const advanceBookingsResponse = await fetchAdvanceBookings();
        setAdvanceBookings(advanceBookingsResponse);

        // Fetch appointments
        const appointmentsResponse = await fetchAppointments();
        setAppointments(appointmentsResponse);

        // Fetch tickets
        const ticketsResponse = await fetchTickets();
        setTickets(ticketsResponse);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const fetchUserData = async (token) => {
    const response = await fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    });
    return response.json();
  };

  const fetchUserEventCount = async (token) => {
    const response = await fetch(API_ROUTES.getUserEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    });
    return response.json();
  };

  const fetchJoinedEventsCount = async (token) => {
    const response = await fetch(API_ROUTES.getJoinedEventsCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    });
    return response.json();
  };

  const fetchRegistrationsCount = async (token) => {
    const response = await fetch(API_ROUTES.getRegistrationAllEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    });
    return response.json();
  };

  const fetchEventAttendees = async (token) => {
    const response = await fetch(API_ROUTES.getAllEventAllAttendeeData, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    });
    return response.json();
  };

  const fetchAdvanceBookings = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/advance_tickets'); // Replace with your API endpoint
      return response.data;
    } catch (error) {
      console.error('Error fetching advance bookings:', error);
      return [];
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await axios.get(API_ROUTES.getAppointments);
      return response.data;
    } catch (error) {
      console.error('Error fetching appointments:', error);
      return [];
    }
  };

  const fetchTickets = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/advance_tickets'); // Replace with your API endpoint
      return response.data;
    } catch (error) {
      console.error('Error fetching tickets:', error);
      return [];
    }
  };

  const formatTime = (timeString) => {
    const eventTime = new Date(timeString);
    return eventTime.toLocaleString(); // Adjust locale and options as needed
  };


  return (
    <div className="homepage-container">
      {/* Navbar */}
      <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />

      {/* Main Content */}
      <div className="main-content">
        {/* Top Bar with Search and Icons */}
        <div className="top-bar">
          <div className="search-bar">
            <div className="search-input-container">
              <input
                type="text"
                placeholder="Search..."
                className="search-input"
              />
            </div>
          </div>
          <div className="right-icons">
            <button className="icon-btn">
              <img src={settingsImg} alt="Settings" className="nav-icon" />
            </button>
            <button className="icon-btn">
              <img src={premiumIcon} alt="Premium" className="nav-icon" />
            </button>
          </div>
        </div>

        {/* Dashboard Panel */}
        <div className="dashboard-panel">
          <h2>Dashboard</h2>
          <div className="data-boxes">
            <div className="data-box">
              <h3>Quarries</h3>
              <p>{registrationsCount}</p>
            </div>
            <div className="data-box">
              <h3>My Events</h3>
              <p>{joinedEventsCount}</p>
            </div>
            {userData && (
              <div className="data-box">
                <h3>Appointments</h3>
                <p>{userData.phoneNumberCount}</p>
              </div>
            )}
          </div>

          {/* Line Graph */}
          <div className="line-graph">
            <LineGraph />
          </div>

          {/* Registrations Section */}
          <div className="footer">
  <div className="advance-bookings">
    <h3>Advance Bookings</h3>
    <ul className="booking-list">
      {tickets.map((ticket) => (
        <li key={index}>
          <span className="booking-email">{ticket.customer_email}</span>
          <span className="booking-phone">{ticket.sender_id}</span>
          <span className="booking-date">{formatTime(ticket.created_at)}</span>
        </li>
      ))}
    </ul>
  </div>
  <div className="appointments">
    <h3>Appointments</h3>
    <ul className="appointment-list">
      {appointments.map((appointment, index) => (
        <li key={index}>
          <span className="appointment-phone">{appointment.phone_number}</span>
          <span className="appointment-date">{formatTime(appointment.created_at)}</span>
          <button className="btn-chat">Chat</button>
        </li>
      ))}
    </ul>
  </div>
</div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
