import React, { useState, useEffect } from 'react';
import './home.css'; // Import your CSS file for styling
import { useNavigate, useLocation } from 'react-router-dom';
import LineGraph from './graph';
import searchIcon from '../images/search.png'; // Adjust the path to your image
import { API_ROUTES } from '../app modules/apiRoutes';
import Navbar from '../componenets/Navbar';
import settingsImg from '../images/settings.png';
import premiumIcon from '../images/premium.png';
import axios from 'axios';

const HomePage = () => {
  const [user, setUser] = useState({});
  const [registrations, setRegistrations] = useState([]);
  const [joinedEventsCount, setJoinedEventsCount] = useState(0);
  const [registrationsCount, setRegistrationsCount] = useState(0);
  const navigate = useNavigate();
  const [activeLink, setActiveLink] = useState('/');
  const pathActive = useLocation();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [advanceBookings, setAdvanceBookings] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [error, setError] = useState(null);



  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await axios.get(API_ROUTES.getAppontments);
        setAppointments(response.data);
      } catch (error) {
        console.error('Error fetching appointments:', error);
      }
    };

    const fetchTickets = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/advance_tickets'); // Replace with your API endpoint
        setTickets(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching advance tickets:', error);
        setError('Error fetching data. Please try again later.');
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);
  useEffect(() => {
    // Fetch token from localStorage
    const token = localStorage.getItem('token');

    // Fetch user details
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));

    // Fetch user events
    fetch(API_ROUTES.getUserEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUserData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
        setLoading(false);
      });

    // Fetch joined events count
    fetch(API_ROUTES.getJoinedEventsCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setJoinedEventsCount(data.count);
      })
      .catch(error => console.error('Error fetching joined events count:', error));

    // Fetch registrations count
    fetch(API_ROUTES.getRegistrationAllEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setRegistrationsCount(data.phoneNumberCount);
      })
      .catch(error => console.error('Error fetching registrations count:', error));

    // Fetch event attendees
    fetch(API_ROUTES.getAllEventAllAttendeeData, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setRegistrations(data);
      })
      .catch(error => console.error('Error fetching event attendees:', error));

  }, []);

  // Function to handle navigation and update active link
  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  // Function to handle navigation and update active link
  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };

  const formatTime = (timeString) => {
    const eventTime = new Date(timeString);
    return eventTime.toLocaleString(); // Adjust locale and options as needed
  };


  return (
    <div className="homepage-container">
      {/* Navbar */}
      <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />

      {/* Main Content */}
      <div className="main-content">
        {/* Top Bar with Search and Icons */}
        <div className="top-bar">
          <div className="search-bar">
            <div className="search-input-container">
              <input
                type="text"
                placeholder="Search..."
                className="search-input"
              />
            </div>
          </div>
          <div className="right-icons">
            <button className="icon-btn">
              <img src={settingsImg} alt="Settings" className="nav-icon" />
            </button>
            <button className="icon-btn">
              <img src={premiumIcon} alt="Premium" className="nav-icon" />
            </button>
          </div>
        </div>

        {/* Dashboard Panel */}
        <div className="dashboard-panel">
          <h2>Dashboard</h2>
          <div className="data-boxes">
            <div className="data-box">
              <h3>Quarries</h3>
              <p>{registrationsCount}</p>
            </div>
            <div className="data-box">
              <h3>My Events</h3>
              <p>{joinedEventsCount}</p>
            </div>
            {userData && (
              <div className="data-box">
                <h3>Appointments</h3>
                <p>{userData.phoneNumberCount}</p>
              </div>
            )}
          </div>

          {/* Line Graph */}
          <div className="line-graph">
            <LineGraph />
          </div>

          {/* Registrations Section */}
          <div className="footer">
  <div className="advance-bookings">
    <h3>Advance Bookings</h3>
    <ul className="booking-list">
      {tickets.map((ticket) => (
        <li key={index}>
          <span className="booking-email">{ticket.customer_email}</span>
          <span className="booking-phone">{ticket.sender_id}</span>
          <span className="booking-date">{formatTime(ticket.created_at)}</span>
        </li>
      ))}
    </ul>
  </div>
  <div className="appointments">
    <h3>Appointments</h3>
    <ul className="appointment-list">
      {appointments.map((appointment, index) => (
        <li key={index}>
          <span className="appointment-phone">{appointment.phone_number}</span>
          <span className="appointment-date">{formatTime(appointment.created_at)}</span>
          <button className="btn-chat">Chat</button>
        </li>
      ))}
    </ul>
  </div>
</div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
