import React, { useState } from 'react';
import './createevent.css';
import { API_ROUTES } from '../app modules/apiRoutes';
import premiumIcon from '../images/premium.png';
import Navbar from '../components/Navbar';

const CreateEventPage = () => {
  const [user, setUser] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [activeLink, setActiveLink] = useState('/');
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    eventName: '',
    description: '',
    eventType: '',
    eventDate: '',
    startTime: '',
    endTime: '',
    location: '',
    registrationType: '',
    ticketTypes: [],
    maxAttendees: '',
    ageRestrictions: '',
    customFields: '',
    visibility: '',
    eventBanner: '',
    sessions: [],
    liveStreaming: false,
    networkingTools: false,
    socialSharing: false,
    discountsPromotions: false,
    emailInvitations: false,
    premiumSubscription: false,
    customCSS: '',
  });

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data:', formData);
    // Example: You can send this data to your backend or perform other actions
  };

  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };

  const pathActive = useLocation();

  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, []);

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleSubscribe = () => {
    console.log('Redirecting to subscription page...');
    setShowModal(false);
    navigate('/premium');
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const nextStep = () => {
    setCurrentStep(prevStep => prevStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(prevStep => prevStep - 1);
  };

  const { eventName, description, eventType, eventDate, startTime, endTime, location, registrationType, ticketTypes, maxAttendees, ageRestrictions, customFields, visibility, eventBanner, sessions, liveStreaming, networkingTools, socialSharing, discountsPromotions, emailInvitations, premiumSubscription, customCSS } = formData;

  switch (currentStep) {
    case 1:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 1: Basic Information</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="eventName">Event Name:</label>
              <input
                type="text"
                id="eventName"
                name="eventName"
                value={eventName}
                onChange={handleChange}
                required
              />

              <label htmlFor="eventBanner">Event Banner:</label>
              <input
                type="file"
                id="eventBanner"
                name="eventBanner"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  // Handle file upload logic here
                }}
              />

              <label htmlFor="description">Description:</label>
              <textarea
                id="description"
                name="description"
                value={description}
                onChange={handleChange}
                required
              ></textarea>

              <label htmlFor="eventType">Event Type:</label>
              <select
                id="eventType"
                name="eventType"
                value={eventType}
                onChange={handleChange}
                required
              >
                <option value="">Select Event Type</option>
                <option value="Conference">Conference</option>
                <option value="Workshop">Workshop</option>
                <option value="Party">Party</option>
                {/* Add more event types as needed */}
              </select>

              <button type="button" onClick={nextStep}>Next</button>
            </form>
          </div>
        </div>
      );

    case 2:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 2: Date and Time</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="eventDate">Event Date:</label>
              <input
                type="date"
                id="eventDate"
                name="eventDate"
                value={eventDate}
                onChange={handleChange}
                required
              />

              <label htmlFor="startTime">Start Time:</label>
              <input
                type="time"
                id="startTime"
                name="startTime"
