import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './createevent.css';
import { API_ROUTES } from '../app modules/apiRoutes';
import premiumIcon from '../images/premium.png';
import Navbar from '../componenets/Navbar';

const CreateEventPage = () => {
  const [user, setUser] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [activeLink, setActiveLink] = useState('/');
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    eventName: '',
    eventDate: '',
  });
  const [sessions, setSessions] = useState([]);  // Initialize sessions state
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data:', formData);
    // Example: You can send this data to your backend or perform other actions
  };

  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };

  const pathActive = useLocation();

  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, []);

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleSubscribe = () => {
    console.log('Redirecting to subscription page...');
    setShowModal(false);
    navigate('/premium');
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };
  

  const nextStep = () => {
    setCurrentStep(prevStep => prevStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(prevStep => prevStep - 1);
  };

  const { eventName, description, eventType, eventDate, startTime, endTime, location, registrationType, ticketTypes, maxAttendees, ageRestrictions, customFields, visibility, eventBanner, liveStreaming, networkingTools, socialSharing, discountsPromotions, emailInvitations, premiumSubscription, customCSS } = formData;

      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 1: Basic Information</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="eventName">Event Name:</label>
              <input
                type="text"
                id="eventName"
                name="eventName"
                value={eventName}
                onChange={handleChange}
                required
              />
 <label htmlFor="eventDate">Event Date:</label>
              <input
                type="date"
                id="eventDate"
                name="eventDate"
                value={eventDate}
                onChange={handleChange}
                required
              />
              <button type="su" onClick={nextStep}>Next</button>
            </form>
          </div>
        </div>
      );
};

export default CreateEventPage;
