import React, { useState, useEffect } from 'react';
import './home.css'; // Import your CSS file for styling
import { useNavigate, useLocation } from 'react-router-dom';
import LineGraph from './graph';
import searchIcon from '../images/search.png'; // Adjust the path to your image
import { API_ROUTES } from '../app modules/apiRoutes';
import Navbar from '../componenets/Navbar';
import settingsImg from '../images/settings.png';
import premiumIcon from '../images/premium.png';

const HomePage = () => {
  const [user, setUser] = useState({});
  const [registrations, setRegistrations] = useState([]);
  const [joinedEventsCount, setJoinedEventsCount] = useState(0);
  const [registrationsCount, setRegistrationsCount] = useState(0);
  const navigate = useNavigate();
  const [activeLink, setActiveLink] = useState('/');
  const pathActive = useLocation();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch token from localStorage
    const token = localStorage.getItem('token');

    // Fetch user details
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));

    // Fetch user events
    fetch(API_ROUTES.getUserEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUserData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
        setLoading(false);
      });

    // Fetch joined events count
    fetch(API_ROUTES.getJoinedEventsCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setJoinedEventsCount(data.count);
      })
      .catch(error => console.error('Error fetching joined events count:', error));

    // Fetch registrations count
    fetch(API_ROUTES.getRegistrationAllEventCount, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setRegistrationsCount(data.count);
      })
      .catch(error => console.error('Error fetching registrations count:', error));

    // Fetch event attendees
    fetch(API_ROUTES.getAllEventAllAttendeeData, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setRegistrations(data);
      })
      .catch(error => console.error('Error fetching event attendees:', error));

  }, []);

  // Function to handle navigation and update active link
  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  // Function to handle navigation and update active link
  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };

  const formatTime = (timeString) => {
    const eventTime = new Date(timeString);
    return eventTime.toLocaleString(); // Adjust locale and options as needed
  };


  return (
    <div className="homepage-container">
      {/* Navbar */}
      <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />

      {/* Main Content */}
      <div className="main-content">
        {/* Top Bar with Search and Icons */}
        <div className="top-bar">
          <div className="search-bar">
            <div className="search-input-container">
              <input
                type="text"
                placeholder="Search..."
                className="search-input"
              />
            </div>
          </div>
          <div className="right-icons">
            <button className="icon-btn">
              <img src={settingsImg} alt="Settings" className="nav-icon" />
            </button>
            <button className="icon-btn">
              <img src={premiumIcon} alt="Premium" className="nav-icon" />
            </button>
          </div>
        </div>

        {/* Dashboard Panel */}
        <div className="dashboard-panel">
          <h2>Welcome, {user.user_name}!</h2>
          <div className="data-boxes">
            <div className="data-box">
              <h3>quarries</h3>
              <p>{registrationsCount}</p>
            </div>
            <div className="data-box">
              <h3>Joined Events</h3>
              <p>{joinedEventsCount}</p>
            </div>
            {userData && (
              <div className="data-box">
                <h3>My Events</h3>
                <p>{userData.eventCount}</p>
              </div>
            )}
          </div>

          {/* Line Graph */}
          <div className="line-graph">
            <LineGraph />
          </div>

          {/* Registrations Section */}
          <div className="registrations">
            <h3>Registrations</h3>
            <ul className="registration-list">
              {registrations.map((registration, index) => (
                <li key={index}>
                  <span className="registration-name">{registration.name}</span>
                  <span className="registration-email">{registration.email}</span>
                  <span className="registration-phone">{registration.phoneNumber}</span>
                  <span className="registration-date">{formatTime(registration.created_at)}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
