import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './createevent.css';
import { API_ROUTES } from '../app modules/apiRoutes';
import premiumIcon from '../images/premium.png';
import Navbar from '../componenets/Navbar';

const CreateEventPage = () => {
  const [user, setUser] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [activeLink, setActiveLink] = useState('/');
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    eventName: '',
    description: '',
    eventType: '',
    eventDate: '',
    startTime: '',
    endTime: '',
    location: '',
    registrationType: '',
    ticketTypes: [],
    maxAttendees: '',
    ageRestrictions: '',
    customFields: '',
    visibility: '',
    eventBanner: '',
    sessions: [],
    liveStreaming: false,
    networkingTools: false,
    socialSharing: false,
    discountsPromotions: false,
    emailInvitations: false,
    premiumSubscription: false,
    customCSS: '',
  });
  const [sessions, setSessions] = useState([]);  // Initialize sessions state
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data:', formData);
    // Example: You can send this data to your backend or perform other actions
  };

  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };

  const pathActive = useLocation();

  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, []);

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleSubscribe = () => {
    console.log('Redirecting to subscription page...');
    setShowModal(false);
    navigate('/premium');
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };
  

  const nextStep = () => {
    setCurrentStep(prevStep => prevStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(prevStep => prevStep - 1);
  };

  const { eventName, description, eventType, eventDate, startTime, endTime, location, registrationType, ticketTypes, maxAttendees, ageRestrictions, customFields, visibility, eventBanner, liveStreaming, networkingTools, socialSharing, discountsPromotions, emailInvitations, premiumSubscription, customCSS } = formData;

  switch (currentStep) {
    case 1:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 1: Basic Information</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="eventName">Event Name:</label>
              <input
                type="text"
                id="eventName"
                name="eventName"
                value={eventName}
                onChange={handleChange}
                required
              />

              <label htmlFor="eventBanner">Event Banner:</label>
              <input
                type="file"
                id="eventBanner"
                name="eventBanner"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  // Handle file upload logic here
                }}
              />

              <label htmlFor="description">Description:</label>
              <textarea
                id="description"
                name="description"
                value={description}
                onChange={handleChange}
                required
              ></textarea>

              <label htmlFor="eventType">Event Type:</label>
              <select
                id="eventType"
                name="eventType"
                value={eventType}
                onChange={handleChange}
                required
              >
                <option value="">Select Event Type</option>
                <option value="Conference">Conference</option>
                <option value="Workshop">Workshop</option>
                <option value="Party">Party</option>
                {/* Add more event types as needed */}
              </select>

              <button type="button" onClick={nextStep}>Next</button>
            </form>
          </div>
        </div>
      );

    case 2:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 2: Date and Time</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="eventDate">Event Date:</label>
              <input
                type="date"
                id="eventDate"
                name="eventDate"
                value={eventDate}
                onChange={handleChange}
                required
              />

              <label htmlFor="startTime">Start Time:</label>
              <input
                type="time"
                id="startTime"
                name="startTime"
                value={startTime}
                onChange={handleChange}
                required
              />

              <label htmlFor="endTime">End Time:</label>
              <input
                type="time"
                id="endTime"
                name="endTime"
                value={endTime}
                onChange={handleChange}
                required
              />

              <button type="button" onClick={prevStep}>Back</button>
              <button type="button" onClick={nextStep}>Next</button>
            </form>
          </div>
        </div>
      );


    case 3:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 4: Tickets and Sessions</h2>
            <form onSubmit={handleSubmit}>
              <h3>Tickets Settings</h3>
              {ticketTypes.map((ticket, index) => (
                <div key={index}>
                  <label htmlFor={`ticketType-${index}`}>Ticket Type {index + 1}:</label>
                  <input
                    type="text"
                    id={`ticketType-${index}`}
                    name={`ticketType-${index}`}
                    value={ticket.name}
                    onChange={(e) => {
                      const updatedTicketTypes = [...ticketTypes];
                      updatedTicketTypes[index].name = e.target.value;
                      setFormData({ ...formData, ticketTypes: updatedTicketTypes });
                    }}
                    placeholder="Ticket Type Name"
                    required
                  />

                  <input
                    type="number"
                    value={ticket.price}
                    onChange={(e) => {
                      const updatedTicketTypes = [...ticketTypes];
                      updatedTicketTypes[index].price = e.target.value;
                      setFormData({ ...formData, ticketTypes: updatedTicketTypes });
                    }}
                    placeholder="Price"
                    required
                  />

                  <input
                    type="number"
                    value={ticket.quantity}
                    onChange={(e) => {
                      const updatedTicketTypes = [...ticketTypes];
                      updatedTicketTypes[index].quantity = e.target.value;
                      setFormData({ ...formData, ticketTypes: updatedTicketTypes });
                    }}
                    placeholder="Quantity"
                    required
                  />

                  <button
                    type="button"
                    onClick={() => {
                      const updatedTicketTypes = [...ticketTypes];
                      updatedTicketTypes.splice(index, 1);
                      setFormData({ ...formData, ticketTypes: updatedTicketTypes });
                    }}
                  >
                    Remove
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => setFormData({ ...formData, ticketTypes: [...ticketTypes, { name: '', price: '', quantity: '' }] })}
              >
                Add Ticket Type
              </button>

              <h3>Sessions Settings</h3>
{sessions.map((session, index) => (
  <div key={index}>
    <label htmlFor={`sessionName-${index}`}>Session Name:</label>
    <input
      type="text"
      id={`sessionName-${index}`}
      value={session.name}
      onChange={(e) => {
        const updatedSessions = [...sessions];
        updatedSessions[index].name = e.target.value;
        setSessions(updatedSessions);
      }}
      placeholder="Session Name"
      required
    />

    <label htmlFor={`sessionStartTime-${index}`}>Start Time:</label>
    <input
      type="time"
      id={`sessionStartTime-${index}`}
      value={session.startTime}
      onChange={(e) => {
        const updatedSessions = [...sessions];
        updatedSessions[index].startTime = e.target.value;
        setSessions(updatedSessions);
      }}
      required
    />

    <label htmlFor={`sessionEndTime-${index}`}>End Time:</label>
    <input
      type="time"
      id={`sessionEndTime-${index}`}
      value={session.endTime}
      onChange={(e) => {
        const updatedSessions = [...sessions];
        updatedSessions[index].endTime = e.target.value;
        setSessions(updatedSessions);
      }}
      required
    />

    <button
      type="button"
      onClick={() => {
        const updatedSessions = [...sessions];
        updatedSessions.splice(index, 1);
        setSessions(updatedSessions);
      }}
    >
      Remove Session
    </button>
  </div>
))}
<button
  type="button"
  onClick={() => setSessions([...sessions, { name: '', startTime: '', endTime: '' }])}
>
  Add Session
</button><br/>

              <button type="button" onClick={prevStep}>Back</button>
              <button type="button" onClick={nextStep}>Next</button>
            </form>
          </div>
        </div>
      );

    case 4:
      return (
        <div className="homepage-container">
          <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
          <div className="event-form-container">
            <h2>Create Event - Step 5: Additional Features</h2>
            <form onSubmit={handleSubmit}>
              <label>Additional Features:</label>
        

              <div className="switch-container">
                <label htmlFor="networkingTools">Networking Tools</label>
                <label className="switch">
                  <input
                    type="checkbox"
                    id="networkingTools"
                    name="networkingTools"
                    checked={networkingTools}
                    onChange={handleChange}
                  />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="switch-container">
                <label htmlFor="discountsPromotions">Discounts and Promotions</label>
                <label className="switch">
                  <input
                    type="checkbox"
                    id="discountsPromotions"
                    name="discountsPromotions"
                    checked={discountsPromotions}
                    onChange={handleChange}
                  />
                  <span className="slider"></span>
                </label>
              </div>

              <div className="switch-container">
                <label htmlFor="emailInvitations">Email Invitations</label>
                <label className="switch">
                  <input
                    type="checkbox"
                    id="emailInvitations"
                    name="emailInvitations"
                    checked={emailInvitations}
                    onChange={handleChange}
                  />
                  <span className="slider"></span>
                </label>
              </div>


              <button type="button" onClick={prevStep}>Back</button>
              <button type="submit">Create Event</button>
            </form>
          </div>
        </div>
      );

    default:
      return null;
  }
};

export default CreateEventPage;
