import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './createevent.css';
import { API_ROUTES } from '../app modules/apiRoutes';
import premiumIcon from '../images/premium.png';
import Navbar from '../componenets/Navbar';

const CreateEventPage = () => {
  const [user, setUser] = useState({});
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();
  const [activeLink, setActiveLink] = useState('/');
  const [eventName, setEventName] = useState('');
  const [description, setDescription] = useState('');
  const [eventType, setEventType] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [location, setLocation] = useState('');
  const [registrationType, setRegistrationType] = useState('');
  const [ticketTypes, setTicketTypes] = useState([]);
  const [maxAttendees, setMaxAttendees] = useState('');
  const [ageRestrictions, setAgeRestrictions] = useState('');
  const [customFields, setCustomFields] = useState('');
  const [visibility, setVisibility] = useState('');
  const [accessibility, setAccessibility] = useState('');
  const [eventBanner, setEventBanner] = useState('');
  const [customCSS, setCustomCSS] = useState('');
  const [sessions, setSessions] = useState([]);
  const [liveStreaming, setLiveStreaming] = useState(false);
  const [networkingTools, setNetworkingTools] = useState(false);
  const [socialSharing, setSocialSharing] = useState(false);
  const [discountsPromotions, setDiscountsPromotions] = useState(false);
  const [emailInvitations, setEmailInvitations] = useState(false);
  const [premiumSubscription, setPremiumSubscription] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log({
      eventName,
      description,
      eventType,
      eventDate,
      startTime,
      endTime,
      location,
      registrationType,
      ticketTypes,
      maxAttendees,
      ageRestrictions,
      customFields,
      visibility,
      accessibility,
      eventBanner,
      customCSS,
      sessions,
      liveStreaming,
      networkingTools,
      socialSharing,
      discountsPromotions,
      emailInvitations,
      premiumSubscription,
    });
    // Example: You can send this data to your backend or perform other actions
  };

  const pathActive = useLocation();

  // Function to handle navigation and update active link
  useEffect(() => {
    setActiveLink(pathActive.pathname);
  }, [pathActive.pathname]);

  // Fetch user details on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch(API_ROUTES.fetchUserDetails, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, []);

  // Modal close handler
  const handleModalClose = () => {
    setShowModal(false);
  };


  const handleNavigation = (path) => {
    navigate(path);
    setActiveLink(path);
  };


  return (
    <div className="homepage-container">
      <Navbar activeLink={activeLink} handleNavigation={handleNavigation} user={user} />
      <div className="event-form-container">
        <h2>Create Event</h2>
        <form onSubmit={handleSubmit}>
          {/* Basic Information */}
          <label htmlFor="eventName">Event Name:</label>
          <input
            type="text"
            id="eventName"
            value={eventName}
            onChange={(e) => setEventName(e.target.value)}
            required
          />

          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>

          <label htmlFor="eventType">Event Type:</label>
          <select
            id="eventType"
            value={eventType}
            onChange={(e) => setEventType(e.target.value)}
            required
          >
            <option value="">Select Event Type</option>
            <option value="Conference">Conference</option>
            <option value="Workshop">Workshop</option>
            <option value="Party">Party</option>
            {/* Add more event types as needed */}
          </select>

          {/* Date and Time */}
          <label htmlFor="eventDate">Event Date:</label>
          <input
            type="date"
            id="eventDate"
            value={eventDate}
            onChange={(e) => setEventDate(e.target.value)}
            required
          />

          <label htmlFor="startTime">Start Time:</label>
          <input
            type="time"
            id="startTime"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            required
          />

          <label htmlFor="endTime">End Time:</label>
          <input
            type="time"
            id="endTime"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            required
          />

          {/* Location */}
          <label>Location:</label>
          <div className="switch-container">
            <label htmlFor="location">Online</label>
            <label className="switch">
              <input
                type="checkbox"
                checked={location === 'Online'}
                onChange={(e) =>
                  setLocation(e.target.checked ? 'Online' : 'Offline')
                }
              />
              <span className="slider"></span>
            </label>
            <label htmlFor="location">Offline</label>
          </div>

          {/* Registration and Tickets */}
          <h3>Registration and Tickets</h3>

          {/* Registration Settings */}
          <label htmlFor="registrationType">Registration Type:</label>
          <div className="switch-container">
            <label htmlFor="registrationType">Open</label>
            <label className="switch">
              <input
                type="checkbox"
                checked={registrationType === 'Open'}
                onChange={(e) =>
                  setRegistrationType(e.target.checked ? 'Open' : 'Closed')
                }
              />
              <span className="slider"></span>
            </label>
            <label htmlFor="registrationType">Closed</label>
          </div>

          {/* Ticket Types */}
          {/* Example of handling multiple ticket types */}
          {ticketTypes.map((ticket, index) => (
            <div key={index}>
              <label htmlFor={`ticketType-${index}`}>Ticket Type {index + 1}:</label>
              <input
                type="text"
                id={`ticketType-${index}`}
                value={ticket.name}
                onChange={(e) => {
                  const updatedTicketTypes = [...ticketTypes];
                  updatedTicketTypes[index].name = e.target.value;
                  setTicketTypes(updatedTicketTypes);
                }}
                placeholder="Ticket Type Name"
                required
              />

              <input
                type="number"
                value={ticket.price}
                onChange={(e) => {
                  const updatedTicketTypes = [...ticketTypes];
                  updatedTicketTypes[index].price = e.target.value;
                  setTicketTypes(updatedTicketTypes);
                }}
                placeholder="Price"
                required
              />

              <input
                type="number"
                value={ticket.quantity}
                onChange={(e) => {
                  const updatedTicketTypes = [...ticketTypes];
                  updatedTicketTypes[index].quantity = e.target.value;
                  setTicketTypes(updatedTicketTypes);
                }}
                placeholder="Quantity"
                required
              />

              <button
                type="button"
                onClick={() => {
                  const updatedTicketTypes = [...ticketTypes];
                  updatedTicketTypes.splice(index, 1);
                  setTicketTypes(updatedTicketTypes);
                }}
              >
                Remove
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={() => setTicketTypes([...ticketTypes, { name: '', price: '', quantity: '' }])}
          >
            Add Ticket Type
          </button>

          {/* Capacity and Restrictions */}
          <label htmlFor="maxAttendees">Maximum Attendees:</label>
          <input
            type="number"
            id="maxAttendees"
            value={maxAttendees}
            onChange={(e) => setMaxAttendees(e.target.value)}
            required
          />

          <label htmlFor="ageRestrictions">Age Restrictions:</label>
          <input
            type="text"
            id="ageRestrictions"
            value={ageRestrictions}
            onChange={(e) => setAgeRestrictions(e.target.value)}
          />

          {/* Advanced Settings */}
          <h3>Advanced Settings</h3>

          <label htmlFor="customFields">Additional Fields:</label>
          <textarea
            id="customFields"
            value={customFields}
            onChange={(e) => setCustomFields(e.target.value)}
            placeholder="Add custom fields (e.g., dietary preferences, special requests)"
          ></textarea>

          <label>Visibility:</label>
          <div className="switch-container">
            <label htmlFor="visibility">Public</label>
            <label className="switch">
              <input
                type="checkbox"
                checked={visibility === 'Public'}
                onChange={(e) =>
                  setVisibility(e.target.checked ? 'Public' : 'Private')
                }
              />
              <span className="slider"></span>
            </label>
            <label htmlFor="visibility">Private</label>
          </div>

          <label>Accessibility:</label>
          <div className="switch-container">
            <label htmlFor="accessibility">Accessible</label>
            <label className="switch">
              <input
                type="checkbox"
                checked={accessibility === 'Accessible'}
                onChange={(e) =>
                  setAccessibility(e.target.checked ? 'Accessible' : 'Not Accessible')
                }
              />
              <span className="slider"></span>
            </label>
            <label htmlFor="accessibility">Not Accessible</label>
          </div>

          {/* Event Banner and Custom CSS */}
          <label htmlFor="eventBanner">Event Banner:</label>
          <input
            type="file"
            id="eventBanner"
            accept="image/*"
            onChange={(e) => {
              const file = e.target.files[0];
              // Handle file upload logic here (e.g., store in state)
            }}
          />

          <label htmlFor="customCSS">Custom CSS:</label>
          <textarea
            id="customCSS"
            value={customCSS}
            onChange={(e) => setCustomCSS(e.target.value)}
            placeholder="Add custom CSS styles for the event page"
          ></textarea>

          {/* Additional Features */}
          <h3>Additional Features</h3>
          <label>
            <div className="switch-container">
              <label htmlFor="liveStreaming">Live Streaming</label>
              <label className="switch">
                <input
                  type="checkbox"
                  id="liveStreaming"
                  checked={liveStreaming}
                  onChange={(e) => setLiveStreaming(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </label>

          <label>
            <div className="switch-container">
              <label htmlFor="networkingTools">Networking Tools</label>
              <label className="switch">
                <input
                  type="checkbox"
                  id="networkingTools"
                  checked={networkingTools}
                  onChange={(e) => setNetworkingTools(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </label>

          <label>
            <div className="switch-container">
              <label htmlFor="socialSharing">Social Sharing</label>
              <label className="switch">
                <input
                  type="checkbox"
                  id="socialSharing"
                  checked={socialSharing}
                  onChange={(e) => setSocialSharing(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </label>

          <label>
            <div className="switch-container">
              <label htmlFor="discountsPromotions">Discounts and Promotions</label>
              <label className="switch">
                <input
                  type="checkbox"
                  id="discountsPromotions"
                  checked={discountsPromotions}
                  onChange={(e) => setDiscountsPromotions(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </label>

          <label>
            <div className="switch-container">
              <label htmlFor="emailInvitations">Email Invitations</label>
              <label className="switch">
                <input
                  type="checkbox"
                  id="emailInvitations"
                  checked={emailInvitations}
                  onChange={(e) => setEmailInvitations(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </label>

          {/* Premium Subscription Requirement */}
          <div className="premium-container">
            <label>
              <div className="switch-container">
                <label htmlFor="premiumSubscription">Premium Subscription Required</label>
                <label className="switch">
                  <input
                    type="checkbox"
                    id="premiumSubscription"
                    checked={premiumSubscription}
                    onChange={(e) => setPremiumSubscription(e.target.checked)}
                  />
                  <span className="slider"></span>
                </label>
              </div>
            </label>
            {premiumSubscription && (
              <div className="premium-info">
                <img src={premiumIcon} alt="Premium Icon" />
                <p>Unlock additional features with a premium subscription!</p>
                <button type="button" onClick={() => setShowModal(true)}>
                  Subscribe Now
                </button>
              </div>
            )}
          </div>

          {/* Submit Button */}
          <button type="submit">Create Event</button>
        </form>
      </div>

  {showModal && (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={handleModalClose}>&times;</span>
        <img src={premiumIcon} alt="Premium Icon" className="premium-icon" />
        <p className="modal-text" style={{ color: '#fff' }}>You need a premium subscription to create events.</p>
        <div className="modal-buttons">
          <button onClick={handleSubscribe} style={{ backgroundColor: '#007bff' }}>Subscribe</button>
          <button onClick={handleModalClose} style={{ backgroundColor: '#007bff' }}>Cancel</button>
        </div>
      </div>
    </div>
  )}
</div>
  );
};

export default CreateEventPage;
